function show_result(data) {
    let result = document.getElementById('result')
    result.innerHTML = ''
    // console.log(data.originalEvent.detail[0].value.map(elem=>elem.join(':')))
    // result.innerHTML += data.originalEvent.detail[0].value.map(elem=>elem.join(': ')).join('<br>')
    const num = data.originalEvent.detail[0]
    num.value.forEach(elem => {
        result.innerHTML += elem.join(': ')
    })
}

jQuery(function($) {
    $("#arm_form").bind("ajax:success", function( xhr,data, status) { show_result(xhr)})
    console.log("Ok")
});
