function show_result(data,xhr,status) {
    console.log(xhr,status)
    let result = document.getElementById('result')
    result.innerHTML = "<br>"
    // console.log(data.originalEvent.detail[0].value.map(elem=>elem.join(':')))
    // result.innerHTML += data.originalEvent.detail[0].value.map(elem=>elem.join(': ')).join('<br>')
    data.originalEvent.detail[0].value.forEach(elem => {
        result.innerHTML += elem.join(': ') + "<br>"
    })
}

jQuery(function($) {
    $("#arm_form").bind("ajax:success", function(data,xhr,status) { show_result(data,xhr,status)})
    console.log("Ok")
});
